import { ActionCreator } from "./store/action";

export const fetchPosts = () => (dispatch) => {
  return getPosts()
    .then((response) => {
      dispatch(ActionCreator.updatePosts(response));
    })
    .then(() => {
      dispatch(ActionCreator.gotPosts());
    });
};
