import { ActionType } from "./action";

const initialState = {
  users: [
    {
      id: 1,
      name: "SEMF UCUK",
      position: "CEO & FOUNDER",
      quote: "Enim irure ea ipsum amet ad commodo Lorem sint.",
      photo: "img/user1.png",
    },
    {
      id: 2,
      name: "DIK ADALIN",
      position: "ENGINEERING",
      quote:
        "Velit do sit tempor exercitation labore esse in culpa consectetur.",
      photo: "img/user2.png",
    },
    {
      id: 3,
      name: "JENG KOL",
      position: "DESIGNER",
      quote: "Elit pariatur anim eu mollit incididunt quis ex amet.",
      photo: "img/user3.png",
    },
    {
      id: 4,
      name: "PET ROMAK",
      position: "MARKETING",
      quote: "Nulla reprehenderit dolore dolor ipsum officia aute.",
      photo: "img/user4.png",
    },
    {
      id: 5,
      name: "JANE GALADRIEL",
      position: "CEO TENGKUREP",
      quote:
        "This is Photoshop's version  of Lorem Ipsum. Proin gravida nibh vel velit auctor aliquet. Aenean sollicitudin, lorem quis bibendum auctor, nisi elit consequat ipsum, nec sagittis sem nibh id elit. Duis sed odio sit amet nibh vulputate cursus a sit amet mauris. Morbi accumsan ipsum velit. Nam nec tellus a odio tincidunt auctor a ornare odio. Sed non  mauris vitae erat consequat auctor eu in elit.",
      photo: "img/user5.png",
    },
  ],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionType.UPDATE_POSTS:
      return Object.assign({}, state, { posts: action.payload.slice(0, 10) });
    default:
      return state;
  }
};

export { rootReducer };
