import ReactPlayer from 'react-player'
import { connect } from 'react-redux';
import closeBtn from "../img/close.png";
import { ActionCreator } from '../store/action';

//const popup = document.querySelector(".popup");

// const popupOpen = document
//     .querySelector(".header__nav-login")
//     .addEventListener("click", () => (popup.style.display = "block"));

// const closeBtn = document
//     .querySelector(".popup__close")
//     .addEventListener("click", () => (popup.style.display = "none"));


function Popup({ showPopup, closePopupBtn }) {
    return !showPopup ? null :
        (
            <div className="popup" >
                <div className="popup__inner">
                    <div className="popup__content">
                        <a className="popup__close" href="#" onClick={closePopupBtn}><img src={closeBtn} width="35px" height="35px"></img></a>
                        <ReactPlayer controls={true} url="https://www.youtube.com/watch?v=KvUgaHTNit4" />
                    </div>
                </div>
            </div>

        )
}

const mapStateToProps = (state) => ({
    showPopup: state.showPopup
})

const mapDispatchToProps = (dispatch) => ({
    closePopupBtn() {
        dispatch(ActionCreator.closePopupBtn())
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(Popup)
